Case.PROCESS_LIMITS:
#PROCESS_LIMITS#
(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) 0 > if{
	0 (>O:XMLVAR_MIN_Limit)
} els{
	0 (>O:XMLVAR_MAX_Limit)
}

#PROCESS_LIMITS#
(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) #OFF_GATE_POS# > if{
	#OFF_GATE_POS# (>O:XMLVAR_MIN_Limit)
}

Condition.PROCESS_LIMITS:
#PROCESS_LIMITS#
(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) #AFTERBURNER_GATE_POS_16K# - abs #GATE_MARGIN# > if{
	(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) #AFTERBURNER_GATE_POS_16K# > if{
		#AFTERBURNER_GATE_POS_16K# (>O:XMLVAR_MIN_Limit)
	} els{
		#AFTERBURNER_GATE_POS_16K# (>O:XMLVAR_MAX_Limit)
	}
}

Case.THROTTLE_ANIM_CODE:
(A:THROTTLE LOWER LIMIT, percent) sp0
(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, Percent) s1 0 < if{
	1 l1 l0 / - 25 * sp2
	0 sp3
	#MAIN_LEVER_REVERSE_POSITION_PCT# sp4
	0 sp5
} els{
	l1 #CLIMB_LEVEL# < if{
		1 sp3
		0 sp4
		#CLIMB_LEVEL# sp5
	} els{
		l1 #FLEX_LEVEL# < if{
			2 sp3
			#CLIMB_LEVEL# sp4
			#FLEX_LEVEL# sp5
		} els{
			3 sp3
			#FLEX_LEVEL# sp4
			#TOGA_LEVEL# sp5
		}
	}
	l1 l4 - l5 l4 - / l3 + 25 * sp2
}

(O:IsDragging) ! if{
	-200 sp6
	l1 l4 - #SNAP_DISTANCE# < if{
		l3 25 * sp6
	} els{
		l5 l1 - #SNAP_DISTANCE# < if{
			l3 1 + 25 * sp6
		}
	}
	
	l6 -200 != if{
		l6 sp2
	}
	
	l2 0.04 * (>#POSITION_TYPE#:#POSITION_VAR#)
}
l2 (>O:XMLVAR_AnimationPosition_PostSnap)
l2

Case.DRAG_VALUE_OVERRIDE:
(M:DragPercent) #OFF_GATE_POS# 0.5 * < (O:XMLVAR_MIN_Limit) #OFF_GATE_POS# < and if{
	(A:FUELSYSTEM VALVE SWITCH:#VALVE_ID#, Boolean) if{
		#VALVE_ID# (>K:FUELSYSTEM_VALVE_CLOSE)
	}
} els{
	(A:FUELSYSTEM VALVE SWITCH:#VALVE_ID#, Boolean) ! if{
		#VALVE_ID# (>K:FUELSYSTEM_VALVE_OPEN)
	}
}

Case.Update:

		(* Keep Track of Airbus Throttle Setting *)
		0 (>L:XML_Airbus_Throttle#ID#_TOGA)
		0 (>L:XML_Airbus_Throttle#ID#_Flex_MCT)
		0 (>L:XML_Airbus_Throttle#ID#_Climb)
		0 (>L:XML_Airbus_Throttle#ID#_Idle)
		0 (>L:XML_Airbus_Throttle#ID#_Rev)
		
		(O:XMLVAR_AnimationPosition_PostSnap) 25 < if{
			1 (>L:XML_Airbus_Throttle#ID#_Rev)
		} els{
			(O:XMLVAR_AnimationPosition_PostSnap) 50 < if{
				1 (>L:XML_Airbus_Throttle#ID#_Idle)
			} els{
				(O:XMLVAR_AnimationPosition_PostSnap) 75 < if{
					1 (>L:XML_Airbus_Throttle#ID#_Climb)
				} els{
					(O:XMLVAR_AnimationPosition_PostSnap) 100 < if{
						1 (>L:XML_Airbus_Throttle#ID#_Flex_MCT)
					} els{
						1 (>L:XML_Airbus_Throttle#ID#_TOGA)
					}
				}
			}
		}
		(O:XMLVAR_AnimationPosition_PostSnap) 25 % 25 / (>L:XML_Airbus_Throttle#ID#_Value)

UseTemplate.DRAG_CODE:
(#POSITION_TYPE#:#POSITION_VAR#) s0 flr sp1 l0 dec sp2
l1 1 < if{
	#MAIN_LEVER_REVERSE_POSITION_PCT# sp4
	0 sp5
} els{
	l1 2 < if{
		0 sp4
		#CLIMB_LEVEL# sp5
	} els{
		l1 3 < if{
			#CLIMB_LEVEL# sp4
			#FLEX_LEVEL# sp5
		} els{
			l1 4 < if{
				#FLEX_LEVEL# sp4
				#TOGA_LEVEL# sp5
			} els{
				#TOGA_LEVEL# sp4
				#TOGA_LEVEL# sp5
			}
		}
	}
}
l5 l4 - l2 * l4 + 163.84 * (>K:THROTTLE#ID#_SET)

UseTemplate.LOCK_CODE:
(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) abs #MIN_DIFF_POS_16K# < if{
	1 (>O:XMLVAR_Limits_Needs_Recalculation)
} els{
	#PROCESS_LIMITS#
}

True.DRAG_CODE:
(M:InputType) 1 == if{
	(O:_ResetLimitsTime) (E:SIMULATION TIME, second) < if{
		(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) abs #MIN_DIFF_POS_16K# < if{
			#RESET_LIMITS#
			1 (>O:XMLVAR_Limits_Needs_Recalculation)
		}
	}
	(E:SIMULATION TIME, second) 0.1 + (>O:_ResetLimitsTime)
}
16384 #MAIN_LEVER_REVERSE_POSITION# - (M:DragPercent) #ADJUST_RAW_DRAG_VALUE# * #MAIN_LEVER_REVERSE_POSITION# + sp0
#DRAG_VALUE_OVERRIDE#
l0 #DRAG_MAX_VALUE# min #DRAG_MIN_VALUE# max 16384 / (>B:ENGINE_Throttle_#ID#_Set)
(O:XMLVAR_Limits_Needs_Recalculation) if{
	(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) abs #MIN_DIFF_POS_16K# > if{
		#PROCESS_LIMITS#
		0 (>O:XMLVAR_Limits_Needs_Recalculation)
	}
}

False.DRAG_CODE:
(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) #INCREMENT# (O:DragScale) * + #DRAG_MAX_VALUE# min #DRAG_MIN_VALUE# max 16384 / (>B:ENGINE_Throttle_#ID#_Set)
(O:XMLVAR_Limits_Needs_Recalculation) if{
	(A:GENERAL ENG THROTTLE LEVER POSITION:#ID#, position 16k) abs #MIN_DIFF_POS_16K# > if{
		#PROCESS_LIMITS#
		0 (>O:XMLVAR_Limits_Needs_Recalculation)
	}
}

Case.PROCESS_LIMITS:
(A:GENERAL ENG PROPELLER LEVER POSITION:#ID#, position 16k) 0 > if{
	0 (>O:XMLVAR_MIN_Limit)
	16384 (>O:XMLVAR_MAX_Limit)
} els{
	#MAIN_LEVER_REVERSE_POSITION# (>O:XMLVAR_MIN_Limit)
	0 (>O:XMLVAR_MAX_Limit)
}

UseTemplate.DOWN_CODE:
(A:GENERAL ENG PROPELLER LEVER POSITION:#ID#, position 16k) 0 == if{
	1 (>O:XMLVAR_Limits_Needs_Recalculation)
} els{
	#PROCESS_LIMITS#
}

UseTemplate.DRAG_CODE:
(M:InputType) 1 == if{
	(O:_ResetLimitsTime) (E:SIMULATION TIME, second) < if{
		(A:GENERAL ENG PROPELLER LEVER POSITION:#ID#, position 16k) abs #MIN_DIFF_POS_16K# < if{
			#RESET_LIMITS#
			1 (>O:XMLVAR_Limits_Needs_Recalculation)
		}
	}
	(E:SIMULATION TIME, second) 0.1 + (>O:_ResetLimitsTime)
}
16384 #MAIN_LEVER_REVERSE_POSITION# - (M:DragPercent) #ADJUST_RAW_DRAG_VALUE# * #MAIN_LEVER_REVERSE_POSITION# + sp0
#DRAG_VALUE_OVERRIDE#
l0 #DRAG_MAX_VALUE# min #DRAG_MIN_VALUE# max 16384 / (>B:ENGINE_Propeller_#ID#_Set)
(O:XMLVAR_Limits_Needs_Recalculation) if{
	(A:GENERAL ENG PROPELLER LEVER POSITION:#ID#, position 16k) abs #MIN_DIFF_POS_16K# > if{
		#PROCESS_LIMITS#
		0 (>O:XMLVAR_Limits_Needs_Recalculation)
	}
}

