UseTemplate.ANIM_CODE:
(A:SIM ON GROUND, Bool) if{
	(A:GEAR STEER ANGLE PCT, Percent) 100 +
} els{
	(A:STEER INPUT CONTROL, Percent) 100 +
}

UseTemplate.UPDATE_CODE:
(O:_TrimDelta) 0 != #ADDITIONAL_TESTS# if{
	(O:_UPDATE_Count) #UPDATE_COUNT_BEFORE_FAST_MODE# >= if{
		(O:_TrimDelta) 0 > if{
			#GET_TRIM# #FAST_MODE_PERCENT_PER_UPDATE# - #SCALE_INCREMENT# #SET_TRIM#
		} els{
			#GET_TRIM# #FAST_MODE_PERCENT_PER_UPDATE# + #SCALE_INCREMENT# #SET_TRIM#
		}
	} els{
		(O:_UPDATE_Count) #UPDATE_COUNT_BEFORE_REPEAT# >= if{
			(O:_TrimDelta) 0 > if{
				#GET_TRIM# #NORMAL_MODE_PERCENT_PER_UPDATE# - #SCALE_INCREMENT# #SET_TRIM#
			} els{
				#GET_TRIM# #NORMAL_MODE_PERCENT_PER_UPDATE# + #SCALE_INCREMENT# #SET_TRIM#
			}
		}
		(O:_UPDATE_Count) 1 + (>O:_UPDATE_Count)
	}
}

True.DRAG_CODE:
(#POSITION_TYPE#:#POSITION_VAR#)  1 <= if{
	(M:DragPercent) (O:PrevDragPercent) - 0 > sp0
	(M:DragPercent) 0 != (M:DragPercent) 0.33 > (M:InputType) 0 == l0 or ? #ARMED_POSITION_INVERSION# if{
		#RETRACT_POSITION# (>#POSITION_TYPE#:#POSITION_VAR#) 
		(>K:SPOILERS_ARM_OFF)
		0 (>K:SPOILERS_SET)
	} els{
		(M:DragPercent) 0 == (M:DragPercent) 0.33 < (M:InputType) 0 == l0 ! and ? #ARMED_POSITION_INVERSION# if{
			#ARMED_POSITION# (>#POSITION_TYPE#:#POSITION_VAR#) 
			(>K:SPOILERS_ARM_ON)
			0 (>K:SPOILERS_SET)
		}
	}
} els{
	(#POSITION_TYPE#:#POSITION_VAR#) 1 - #STEPS_NUMBER# / 16384 * (>B:HANDLING_Spoilers_Set)
	(A:SPOILERS ARMED, Bool) if{ (>K:SPOILERS_ARM_OFF) }
}

True.UPDATE_CODE:
#ARMED_STATE_TEST# if{
	1 (>#POSITION_TYPE#:#POSITION_VAR#) 
} els{
	(A:SPOILERS HANDLE POSITION, Percent over 100) 0 == if{
		0 (>#POSITION_TYPE#:#POSITION_VAR#) 
	} els{
		1 (A:SPOILERS HANDLE POSITION, Percent over 100) #STEPS_NUMBER# * + (>#POSITION_TYPE#:#POSITION_VAR#) 
	}
}

False.UPDATE_CODE:
#ARMED_STATE_TEST# if{
	0 (>#POSITION_TYPE#:#POSITION_VAR#) 
} els{
	1 (A:SPOILERS HANDLE POSITION, Percent over 100) #STEPS_NUMBER# * + (>#POSITION_TYPE#:#POSITION_VAR#) 
}

Condition.COMP_UPDATE_CODE:
(* Handle actual simvar activation *)
(A:SIM ON GROUND, Bool) (A:GROUND VELOCITY, Knots) 10 < and ! sp0
(A:AUTO BRAKE SWITCH CB, Enum) 0 > l0 ! and if{
	0 (>K:SET_AUTOBRAKE_CONTROL)
} els{
	l0 if{ 
		(L:XMLVAR_Autobrake_Level_Armed) (>K:SET_AUTOBRAKE_CONTROL)
	}
}
(* Handle disarming after landing gear up *)
(O:_Countdown_Disable_Autobrake) 0 > if{
	(O:_Countdown_Disable_Autobrake) 1 - (>O:_Countdown_Disable_Autobrake)
	(O:_Countdown_Disable_Autobrake) 0 <= if{
		0 (>L:XMLVAR_Autobrake_Level_Armed)
	}
}
(L:XMLVAR_Autobrake_Level_Armed) 0 > if{
	(A:GEAR HANDLE POSITION, Bool) s0 (O:_Gear_Position) != sp1 l0 (>O:_Gear_Position)
	l1 l0 ! and if{ 
		10 (>O:_Countdown_Disable_Autobrake)
	}
}

Case.ENUM_VAL_TO_POS_EXTERNAL:
l1 if{ #POS_DISARM# sp2 g2 }
els{
	l0 0 == if{ #POS_RTO# sp2 g2 }
	l0 1 == if{ #POS_OFF# sp2 g2 }
	l0 2 == if{ #POS_1# sp2 g2 }
	l0 3 == if{ #POS_2# sp2 g2 }
	l0 4 == if{ #POS_3# sp2 g2 }
	l0 5 == if{ #POS_4# sp2 g2 }
	l0 6 == if{ #POS_MAX_AUTO# sp2 g2 }
} :2 l2

case.ENUM_VAL_TO_POS_EXTERNAL:
l0 if{
	l1 #POWER_INT# == if{ 
		#POS_INT# sp2 
	} els{ 
		l1 #POWER_LOW# == if{ #POS_LOW# sp2  } els{ #POS_HIGH# sp2  }
	} g2
} els{ #POS_OFF# sp2 g2	} :2 l2

l0 if{
	l2 #POWER_FAST# == if{ #POS_FAST# sp3 } els{ #POS_NORM# sp3 } g2
} els{
	l1 if{ #POS_PARK# sp3 } els{ #POS_OFF# sp3 } g2
} :2 l3
